$(document).ready(function(event){
	$("#nav a").on("click", function(){
		$('#top-button a').removeClass('active');	
		$('#about-me-button a').removeClass('active');	
		$('#resume-button a').removeClass('active');	
		$('#contact-button a').removeClass('active');	
	});

	$("#nav  #top-button").on("click", function(){
		$('#top-button a').addClass('active');
		$('html, body').animate({
	    scrollTop: $("#initial-view").offset().top - 90
		}, 1000);
	});

	$("#nav  #about-me-button").on("click", function(){
		$('#about-me-button a').addClass('active');	
		$('html, body').animate({
	    scrollTop: $("#about-me").offset().top - 90
		}, 1000);
	});

	$("#nav  #resume-button").on("click", function(){
		$('#resume-button a').addClass('active');	
		$('html, body').animate({
	    scrollTop: $("#resume").offset().top - 90
		}, 1000);
	});

	$("#nav  #contact-button").on("click", function(){
		$('#contact-button a').addClass('active');	
		$('html, body').animate({
	    scrollTop: $("#contact-me").offset().top - 90
		}, 1000);
	});

	var fixedLimit = $("#about-me").offset().top;

	$(window).bind('scroll', function () {
		var currentTop = $(window).scrollTop();
		if(currentTop <= fixedLimit)
		{
			$('#fixed-container').addClass('hide');			
		}
		else{
			$('#fixed-container').removeClass('hide');	
		}
	});


});

