$(document).ready(function(event){
	window.scrollTo(0,0); //Default at top
	window.sectionArray = [];

	$('.autoplay').slick({
	  slidesToShow: 3,
	  slidesToScroll: 1,
	  autoplay: true,
	  autoplaySpeed: 4000,
	  responsive: [ {
      breakpoint: 1024,
	      settings: {
	        slidesToShow: 3,
	        slidesToScroll: 3,
	        infinite: true,
	        dots: true
	      }
	    },
	    {
	      breakpoint: 600,
	      settings: {
	        slidesToShow: 2,
	        slidesToScroll: 2
	      }
	    }]
	});

	//Get the y-index of the different sections
	$(".content-section").each(function(){
		window.sectionArray.push({ [$(this)[0].id] : $(this)[0].offsetTop});
	});

	$('#fixed-container').hide();
	$('.slide-down-menu').hide();
	$("#nav a").on("click", function(){
		var chosenElement = "#" + $(this).closest("li").attr("id").split("-button")[0];
		var scrollLocation = chosenElement != "#about-me" ? $(chosenElement).offset().top - 90 : $(chosenElement).offset().top;
		$('nav a').removeClass('active');	
		$(this).addClass('active');
		$('html, body').delay(30).stop();
		$('html, body').animate({
	    scrollTop: scrollLocation
		}, 1000);
	});


	var fixedLimit = $("#about-me").offset().top;

	$(window).bind('scroll', function () {
		var currentTop = $(window).scrollTop();
		if(currentTop <= fixedLimit)
		{
			$('#fixed-container').fadeOut(500)
		}
		else{
			$('#fixed-container').fadeIn(500)
		}

		if(window.sectionArray)
		{

		}
	});

	$( window ).resize(function() {
		var windowWidth = $(window).width();
		var slideCount = windowWidth < 700 ? 2 : 3;
		$('.autoplay').slick({
			slidesToShow:slideCount
		});

		$("#nav a").each(function(){
			//debugger;
		});
	});


});

