$(document).ready(function(event){

	$("#nav  #about-me-button").on("click", function(){
		$('html, body').animate({
	    scrollTop: $("#about-me").offset().top
		}, 1000);
	});

	$("#nav  #resume-button").on("click", function(){
		$('html, body').animate({
	    scrollTop: $("#resume").offset().top
		}, 1000);
	});

	$("#nav  #contact-button").on("click", function(){
		$('html, body').animate({
	    scrollTop: $("#contact-me").offset().top
		}, 1000);
	});

	var winterTop = $("#about-me").offset().top;
	var springTop = $("#resume").offset().top;
	var summerTop = $("#contact-me").offset().top;

	$(window).bind('scroll', function () {
		var currentTop = $(window).scrollTop();
		$('html').removeClass();
	    if ( currentTop > winterTop && currentTop <= springTop ) {
	        $('html').addClass('winter-bg');
	    }
	    else if (currentTop > springTop && currentTop <= summerTop) {
	        $('html').addClass('spring-bg');
	    }
	    else if (currentTop > summerTop) {
	        $('html').addClass('summer-bg');
	    }
     	else {
	        $('html').addClass('autumn-bg');
	    }
	});
});

