$(document).ready(function(event){
	window.scrollTo(0,0); //Default at top
	window.sectionArray = [];

	$(".content-section").each(function(){
			window.sectionArray.push({'id' : $(this)[0].id, 'top' : $(this)[0].offsetTop});
		});

	$('#fixed-container').hide();
	$("#nav a").on("click", function(){
		var chosenElement = "#" + $(this).closest("li").attr("id").split("-button")[0];
		var scrollLocation = chosenElement != "#about-me" ? $(chosenElement).offset().top - 90 : $(chosenElement).offset().top + 1;
		$('nav a').removeClass('active');	
		$(this).addClass('active');
		$('html, body').animate({
	    scrollTop: scrollLocation
		}, 1000);
	});


	var fixedLimit = $("#about-me").offset().top;

	$(window).bind('scroll', function () {
		var currentTop = $(window).scrollTop();
		if(currentTop <= fixedLimit)
		{
			$('#fixed-container').fadeOut(500)
		}
		else{
			$('#fixed-container').fadeIn(500)
		}
	});

	$( window ).resize(function() {
		$("#nav a").each(function(){
			//debugger;
		});
	});


});

